using System.Collections; using System.Collections.Generic; using UnityEngine; using UnityEngine.SceneManagement;  public class GamePlay : MonoBehaviour {          void Start()     {         collision_check = false;         mWidth = Screen.width;         mHeight = Screen.height;          float rate_screen = mWidth / mHeight;          mInit_pos = new Vector3(rate_screen*(-2.5f),transform.position.y,transform.position.z);          transform.position = mInit_pos;           //terrain.position= new Vector3(rate_screen * (-5.0f), transform.position.y, transform.position.z);        }      void Update()     {         if (Input.GetMouseButton(0) && collision_check == true)         {             Vector3 velocity = GetComponent<Rigidbody2D>().velocity;              if (velocity.x >= 0)             {                 GetComponent<Rigidbody2D>().AddForce(new Vector3(velocity.x * 2.0f + 0.1f, velocity.y * 2.0f + 0.1f, velocity.z * 2.0f));             }         }           if (transform.position.x >= 140)         {             SceneManager.LoadScene("Main");         }      }      void OnCollisionEnter2D(Collision2D other)     {         if (other.gameObject.tag == "terrain")         {             // Debug.Log(other.relativeVelocity.magnitude);              collision_check = true;              if (other.relativeVelocity.magnitude > 15.0f)             {                  break_object.transform.position =new Vector3(transform.position.x, transform.position.y+1.0f, transform.position.z);                                 break_object.SetActive(true);                  GetComponent<Rigidbody2D>().velocity=new Vector2(0,0);                  GetComponent<Renderer>().enabled = false;                 GetComponent<Rigidbody2D>().isKinematic = true;                  StartCoroutine(WaitAndContinue());             }         }     }     void OnCollisionExit2D(Collision2D other)     {         if (other.gameObject.tag == "terrain")         {             collision_check = false;           }     }       IEnumerator WaitAndContinue()     {         yield return new WaitForSeconds(2.0f);         SceneManager.LoadScene("Main");     }      public bool collision_check;     public float mWidth;     public float mHeight;      public Vector3 mInit_pos;           public GameObject break_object;     public GameObject terrain_objects;     // public float limit_speed= -8.0f;  } 