using System.Collections; using System.Collections.Generic; using UnityEngine;  public class MasterCam : MonoBehaviour { 	 	// Update is called once per frame 	void Update () {          float mWidth= Screen.width;         float mHeight = Screen.height;         float rate = mWidth / mHeight;          transform.position = new Vector3(master.transform.position.x + rate*2.5f, 0.0f, -10f); 	}      public GameObject master; } 